"""
see https://leetcode.com/problems/word-search/
"""
class Solution(object):
    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """
        if not word:
            return True
        if not board:
            return False

        
        for i in range(len(board)):
            for j in range(len(board[0])):
                if self.helper(board, i, j, word):
                    return True
        return False

    def helper(self, ob, i, j, wrd):
        # print(i, j, ob)
        c, w, h = ob[i][j], len(ob[0]), len(ob)
        if c == wrd[0]:
            if len(wrd) == 1:
                return True
            nexts = [(i+1,j), (i-1,j), (i,j+1), (i,j-1)]
            res = []
            ob[i][j] = ""
            for a, b in nexts:
                if 0<=a<h and 0<=b<w and ob[a][b] != "":
                    res += self.helper(ob, a, b, wrd[1:]),
                if any(res):
                    return True
            ob[i][j] = wrd[0]

        else:
            return False

