"""
see https://leetcode.com/problems/word-search/
"""
class Solution(object):
    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """
        if not word:
            return True
        if not board:
            return False

        # seek through the board and apply helper.
        for i in xrange(len(board)):
            for j in xrange(len(board[0])):
                if self.helper(i,j,board,word):
                    return True
        return False

    def helper(self, i, j, board, word):
        # For each grid, check whether it's valid or not.
        # if valid, go check next
        # else, restore the grid.
        if board[i][j] == word[0]:
            board[i][j] = ''
            h, w = len(board), len(board[0])
            coordinate = [(1,0),(-1,0),(0,1),(0,-1)]        # check i, j's neighbor
            for c in coordinate:
                y, x= i+c[0], j+c[1]
                if word[1:] == '':
                    return True
                if y>=0 and y<h and x>=0 and x<w and self.helper(y,x,board,word[1:]):
                    return True
            board[i][j] = word[0]    
        return False

