from typing import List
import collections
class Solution:
    def shortestPath_DFS(self, grid: List[List[int]], k: int) -> int:
        # DFS (690ms)
        H, W = len(grid), len(grid[0])
        if k > (H+W-2): return H+W-2

        minPath = [[[float('inf') for _ in range(k+1)] for _ in range(W)] for _ in range(H)]
        # go all 4 directions
        def dfs(i, j, path, used):
            minPath[i][j][used] = path
            for a, b in [(i+1,j), (i,j+1), (i-1,j), (i,j-1)]:
                if 0<=a<H and 0<=b<W:
                    if grid[a][b] == 0 and minPath[a][b][used] > path+1:
                        dfs(a, b, path+1, used)
                    elif grid[a][b] == 1 and used+1 <= k and minPath[a][b][used+1] > path+1:
                        dfs(a, b, path+1, used+1)
            return
        dfs(0, 0, 0, 0)
        # print(minPath)
        return -1 if min(minPath[-1][-1]) == float('inf') else min(minPath[-1][-1])

    def shortestPath(self, grid: List[List[int]], k: int) -> int:
        # BFS (108ms)
        H, W = len(grid), len(grid[0])
        if k > (H+W-2): return H+W-2

        dq = collections.deque([(0,0,0,0)])
        visited = set([(0,0,0)])
        while dq:
            i, j, path, used = dq.popleft()
            if i == H-1 and j == W-1: return path
            for a, b in [(i+1,j), (i,j+1), (i-1,j), (i,j-1)]:
                if 0<=a<H and 0<=b<W:
                    if grid[a][b] == 0 and (a,b,used) not in visited:
                        visited.add((a,b,used))
                        dq.append([a, b, path+1, used])
                    elif grid[a][b] == 1 and used+1 <= k and (a,b,used+1) not in visited:
                        visited.add((a,b,used+1))
                        dq.append([a, b, path+1, used+1])
        return -1

